mapper.update:
update store."goods"
stage= #{stage}, goods_id= #{goods.goodsId}, buyer_id= #{buyer.userId}, seller_id= #{seller.userId}, mailing_number= #{mailingNumber}, shipping_address= #{shippingAddress},
where goods_id= #{goodsId}

mapper.select:
select need_mailing is not null
from store.deal
where deal_id = #{dealId};
